+            type="text"
+            name="contributedBy"
+            placeholder="Contributed by"
+            value={contributedBy}
+            onChange={handleContributedBy}
+          />
+          <button className="btn btn-primary btn-round">Add Beer</button>
+        </form>
+      </div>
+    </>
+  );
+}
+
+export default AddBeerPage;

