import React, { Component } from 'react'
import BeersServices from './../../../service/beer.service'
import { Container, Row, Button, Modal } from 'react-bootstrap'

import './Beer-list.css'

class BeerList extends Component {

    constructor() {
        super()
        this.state = {
            beers: undefined,

        }

        this.beerServices = new BeersServices()


    }

            componentDidMount = () => this.refreshBeers()

            refreshBeers = () => {
                this.beerServices
                                .getBeer()
                                 .then(res => this.setState({ beers: res.data }))
                                .catch(err => console.log(err))
            }

    render() {
        return (

            <>
                <Container>

                    <h1>Listado de cervezas</h1>

                    <Button onClick={() => this.handleModal(true)} variant="dark" size="sm">Crear nueva monta√±a rusa</Button>

                    <Row>
                        {
                            this.state.coasters
                                ?
                                this.state.coasters.map(elm => <CoasterCard key={elm._id} {...elm} />)
                                :
                                <Loader />
                        }
                    </Row>
                </Container>


                <Modal show={this.state.showModal} onHide={() => this.handleModal(false)}>
                    <Modal.Body>
                        <CoasterForm closeModal={() => this.handleModal(false)} updateList={this.refreshCoasters} />
                    </Modal.Body>
                </Modal>

            </>
        )
    }
}

export default BeerList
