import React, { Component } from 'react'
import BeersServices from './../../../service/beer.service'
import { Container, Row, Button, Modal } from 'react-bootstrap'

import './Beer-list.css'

class BeerList extends Component {

    constructor() {
        super()
        this.state = {
            beers: undefined,

        }

        this.beerServices = new BeersServices()


        componentDidMount = () => this.refreshCoasters()

        refreshCoasters = () => {
            this.coastersService
                .getCoasters()
                .then(res => this.setState({ coasters: res.data }))
                .catch(err => console.log(err))
        }

    }

    render() {
        return (
            <>

                <h1>HOLA SOY LA BEER LIST</h1>


            </>
        )
    }
}

export default BeerList
