import React, { Component } from 'react';
import styled from '@emotion/styled';

const CardStyle = styled.div`
width: 100%;
heigth: auto;
`

class Card extends Component {
  render() {
    return (
      <CardStyle>
        <img src={this.props.img} />
        <h2>{this.props.title}</h2>
        <p>{this.props.description}</p>
      </CardStyle>
    )
  }
};

export default Card;
