import React, { Component } from 'react';
import Navbar from "./Beers";
import {Link} from "react-router-dom";
import axios from 'axios';

class Find extends Component{

  findBeer = () => {
    let query,search;
    axios.get(`https://ironbeer-api.herokuapp.com/beers/search?q=${query}`)
      .then(res=>{
        search=res.data;
      })

      .catch(err=>{
        console.log(err)
      })
  }

  render() {
    return(
      <div>
        <Navbar/>

        <div className="card">
          <Link to={`/${props.beer._id}`}>
            <img className="circle responsive-img"  src={props.beer.image_url} width="20" alt=""/>
            <h5 className="headerProf"> {props.beer.name}</h5>
            <h6>{props.beer.tagline}</h6>
            <p>{props.beer.contributed_by}</p>
          </Link>
        </div>
      </div>
    );
  }
}
export default Find;
