import React, { Component } from 'react';
import {Link} from "react-router-dom";
import axios from 'axios';
import Navbars from "./Navbar";
import {InputGroup, FormControl, Row, Col,Button} from "react-bootstrap";
import DetailBeer from "./DetailBeer";

class Find extends Component{

  state={
    click:false
  }

  findBeer = () => {
    alert('entreoz')
    let query,search;
    let {click} = this.state;

    axios.get(`https://ironbeer-api.herokuapp.com/beers/search?q=${query}`)
      .then(res=>{
        search=res.data;
      })

      .catch(err=>{
        console.log(err)
      })

      this.setState({click:!click})
  }

  render() {
    let {search,click} = this.state;
    return(
      <div>
       <Navbars/>
        <Row>
          <Col xs={4}>
          </Col>
          <Col xs={4}>
            <InputGroup className="mt-5">
              <InputGroup.Prepend>
                <Button
                  onClick={this.findBeer}
                  variant="outline-success"
                >Search</Button>
              </InputGroup.Prepend>
              <FormControl aria-describedby="basic-addon1" />
            </InputGroup>
            {
              click===true
                ? <DetailBeer
                  beer={search}
                />
                :""
            }

          </Col>
            <Col xs={4}>
            </Col>
        </Row>

      </div>
    );
  }
}
export default Find;
