import React, { Component } from "react";
import axios from "axios";
import { Switch, Route, Redirect } from "react-router-dom";
import { Link } from "react-router-dom";

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      allMovies: [],
      randomMovie: [],

      filteredMovies: []
    };
  }
  componentDidMount() {
    axios.get("http://localhost:5000/all").then(movies => {
      this.allMovies = movies.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        allMovies: movies.data
        // filteredMovies: movies.data
      });
    });
    axios.get("http://localhost:5000/random").then(movie => {
      this.randomMovie = movie.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        randomMovie: movie.data
        // filteredMovies: movie.data
      });
    });
  }

  render() {
    console.log(this.state.allMovies);
    console.log(this.state.randomMovie);

    return (
      <section>
        <Switch>
            <Route exact path='/movies' render={() => {
              return <Home movies={this.state.movies}></Home>
            }} />
            <Route exact path='/viewMovie/:chosenMovie' render={(props) => {
              var chosenMovie = props.match.params.chosenMovie
              return <MovieDetail movie={this.state.movies[chosenMovie]}></MovieDetail>
            }} />

          </Switch>

          <Link to={"/beers/"}>
              <img src="../public/img/beers.png" alt="allBeers"/>
              <h2>All Beers</h2>
              </Link>
     </section>

    );
  }
}
