import React, { Component } from "react";
import axios from "axios";
import { Switch, Route, Redirect } from "react-router-dom";
import { Link } from "react-router-dom";
import Beer from "./components/Beer"

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      allBeers: [],
      randomBeer: [],

      filteredBeers: []
    };
  }
  componentDidMount() {
    axios.get("http://localhost:5000/all").then(bbers => {
      this.allBeers = beers.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        allBeers: beers.data
        // filteredBeers: movies.data
      });
    });
    axios.get("http://localhost:5000/random").then(movie => {
      this.randomBeer = movie.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        randomBeer: movie.data
        // filteredBeers: movie.data
      });
    });
  }

  render() {
    console.log(this.state.allMovies);
    console.log(this.state.randomMovie);

    return (
      <section>
        <div className="root">
          <h1 class="list-group-item list-group-item-action col-12 active">
            WikiCountries
          </h1>
          <div className="list-group-item list-group-item-action col-5">
            {this.state.list.map((country, idx) => (
              <div className="list-group">
                <Link
                  to={"/countries/" + idx}
                  className="list-group-item list-group-item-action"
                >
                  {country.name.common}
                  {country.flag}
                </Link>
              </div>
            ))}
          </div>
          {/* <header className="App-header"> */}
            <Switch>
              <Route
                exact
                path="/countries/:chosenCountry"
                render={props => {
                  var chosenCountry = props.match.params.chosenCountry;
                  return (
                    <Beer country={this.state.randomBeer} function={this.findBorderCountry()} />
                  );
                }}
              />
            </Switch>
          {/* </header> */}
        </div>
      </section>
    );
  }
}
