import React, { Component } from "react";
import axios from "axios";
import { Switch, Route, Redirect } from "react-router-dom";
import { Link } from "react-router-dom";

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      allMovies: [],
      randomMovie: [],

      filteredMovies: []
    };
  }
  componentDidMount() {
    axios.get("http://localhost:5000/all").then(movies => {
      this.allMovies = movies.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        allMovies: movies.data
        // filteredMovies: movies.data
      });
    });
    axios.get("http://localhost:5000/random").then(movie => {
      this.randomMovie = movie.data;

      this.setState({
        ...this.state,
        // filterQuery: "",
        cart: [],
        randomMovie: movie.data
        // filteredMovies: movie.data
      });
    });
  }

  render() {
    console.log(this.state.allMovies);
    console.log(this.state.randomMovie);

    return (
      <section>
        <div className="row">
          <h1>WikiCountries</h1>
          <div>
            <div className="list-group">
              <Link to={"/beers/"} />
            </div>
            ))}
          </div>
          {/* <header className="App-header"> */}
          <Switch>
            <Route
              exact
              path="/countries/:chosenCountry"
              render={props => {
                var chosenCountry = props.match.params.chosenCountry;
                return (
                  <h1>Hooola</h1> // <CountryDetail country={this.state.list[chosenCountry]} function={this.findBorderCountry()} />
                );
              }}
            />
          </Switch>
          {/* </header> */}
        </div>
      </section>
    );
  }
}
