import React, {Component} from 'react'
import {Link} from "react-router-dom";
import {Button, Card, Col, FormControl, InputGroup, Row} from "react-bootstrap";
import axios from "axios";
import Navbars from "./Navbar";
import DetailBeer from "./Find";

class SingleBeer extends Component{

  state={
    beer: []
  }
  componentWillMount() {
    let {beer}= this.state
    let id=this.props.match.params.id;
    console.log(id)
    axios.get(`https://ironbeer-api.herokuapp.com/beers/single/${id}`)
      .then(res=>{
        beer=res.data
        this.setState({beer})
        console.log('beer-axios',beer)
      });
  }

  render() {
    let beer= this.state;
    if ( beer!==undefined){
      console.log('entro',beer.beer.name)
    }

    return(
        <div>
          <Navbars/>

          <Row>
            <Col xs={4}>
            </Col>
            <Col xs={4}>
              {
                beer.length!==0?
                  <Card className="mt-5" style={{ width: '18rem' }}>
                    <Link to={`single/${beer.beer._id}`}>
                      <Card.Img variant="top"  className="img"  src={beer.beer.image_url} />
                      <Card.Body>
                        <Card.Title>{beer.beer.name}</Card.Title>
                        <Card.Text>
                          <h6>{beer.beer.tagline}</h6>
                          <p>{beer.beer.contributed_by}</p>
                        </Card.Text>
                      </Card.Body>
                    </Link>
                  </Card>
                  :""
              }

            </Col>
            <Col xs={4}>
            </Col>
          </Row>


        </div>
      )
    }
}

export default SingleBeer
