import React, {Component} from 'react';
import axios from "axios";



class Beers extends Component{

  state={
    beer:""
  }
  componentWillMount() {
    console.log('componentWillMount')

    axios.get('https://ironbeer-api.herokuapp.com/beers/all')
      .then(res=>{
        let {beer}=this.state
        beer=res;
        this.setState({beer})
        console.log(beer)
      })

      .catch(err=>{
        console.log(err)
      })
  }

  render() {
    let data = {...this.state.beer}
    return(
      <div>
        <h1>beer</h1>
        {/*{*/}
          {/*console.log("nada=>",data.data)*/}

        {/*}*/}
        {
          data.data.map((el,i)=>

              el.name[i]

          )
        }
      </div>
    )
  }
}


export default Beers
